    while (i < components.length) {
      if (components[i] == '.') {
        components.splice(i, 1);
      } else if (
          i && components[i] == '..' && components[i - 1] &&
          components[i - 1] != '..') {
        components.splice(--i, 2);
      } else {
        i++;
