  /**
   * FIXME empty description for jsdoc
   */
  ol.Kinetic.prototype.begin = function() {
    this.points_.length = 0;
    this.angle_ = 0;
    this.initialVelocity_ = 0;
  };


  /**
   * @param {number} x X.
   * @param {number} y Y.
   */
