  ol.interaction.DragBox.prototype.onBoxEnd = ol.nullFunction;


  /**
   * @param {ol.MapBrowserPointerEvent} mapBrowserEvent Event.
   * @return {boolean} Stop drag sequence?
   * @this {ol.interaction.DragBox}
   * @private
