        var layerRenderer = /** @type {ol.renderer.canvas.Layer} */ (this.getLayerRenderer(layer));
        result = layerRenderer.forEachLayerAtCoordinate(
            coordinate, frameState, callback, thisArg);
        if (result) {
          return result;
        }
