      button.className = this.cssClassName_ + '-true';
      ol.dom.replaceNode(this.labelActiveNode_, this.labelNode_);
    } else {
      button.className = this.cssClassName_ + '-false';
      ol.dom.replaceNode(this.labelNode_, this.labelActiveNode_);
