   * @api
   */
  ol.geom.GeometryCollection = function(opt_geometries) {

    ol.geom.Geometry.call(this);

    /**
     * @private
     * @type {Array.<ol.geom.Geometry>}
     */
    this.geometries_ = opt_geometries ? opt_geometries : null;

    this.listenGeometriesChange_();
  };
