    if (this.condition_(mapBrowserEvent)) {
      mapBrowserEvent.map.getView().setHint(ol.ViewHint.INTERACTING, 1);
      this.lastAngle_ = undefined;
      this.lastMagnitude_ = undefined;
      return true;
    } else {
      return false;
    }
  };

